<script setup lang="ts">
import { breakpointsTailwind } from '@vueuse/core'
import { useMainStore } from '~/stores/main'
import gsap from 'gsap'

const mainStore = useMainStore()

const isMobile = useBreakpoints(breakpointsTailwind).smaller('md')
const isMenuAnimating = ref(false)
const isDark = ref(true)
const isMenuOpen = ref(false)

const bodyRef = ref<HTMLElement>()
const overlayPath = ref<HTMLElement>()
const menuLinks = ref<HTMLElement>()
const bgRef = ref<HTMLElement>()

const openMenu = () => {
  if (isMenuAnimating.value) return

  isMenuOpen.value = true
  isDark.value = false
  isMenuAnimating.value = true
  const onComplete = () => {
    isMenuAnimating.value = false
  }
  const tl = isMobile.value
    ? gsap
        .timeline()
        .set(overlayPath.value!, {
          attr: { d: 'M 0 0 V 0 Q 50 0 100 0 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.8,
          ease: 'power4.in',
          attr: { d: 'M 0 0 V 70 Q 50 80 100 70 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.3,
          ease: 'power2',
          attr: { d: 'M 0 0 V 100 Q 50 100 100 100 V 0 z' },
        })
        .to(
          menuLinks.value!.querySelectorAll('.menu-link'),
          {
            duration: 0.8,
            ease: 'power3.out',
            y: 0,
            rotate: 0,
            stagger: 0.05,
          },
          0.7
        )
    : gsap
        .timeline()
        .set(overlayPath.value!, {
          attr: { d: 'M 0 0 V 0 Q 50 0 100 0 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.8,
          ease: 'power4.out',
          attr: { d: 'M 0 0 V 50 Q 50 70 100 50 V 0 z' },
        })
        .to(
          menuLinks.value!.querySelectorAll('.menu-link'),
          {
            duration: 0.8,
            ease: 'power3.out',
            y: 0,
            rotate: 0,
            stagger: 0.05,
          },
          0.2
        )
        .fromTo(
          bodyRef.value!,
          {
            filter: 'blur(0px)',
          },
          {
            filter: 'blur(20px)',
            duration: 1,
          },
          0
        )
  tl.to(
    '#menu-1',
    { y: '0.6vh', rotate: -45, ease: 'power2.out', duration: 0.2 },
    0
  )
    .to(
      '#menu-2',
      { y: '-0.6vh', rotate: 45, ease: 'power2.out', duration: 0.2 },
      0
    )
    .eventCallback('onComplete', onComplete)
}

const suspendMenu = () => {}

const closeMenu = () => {
  if (isMenuAnimating.value) return

  isMenuAnimating.value = true
  const onComplete = () => {
    isDark.value = true
    isMenuAnimating.value = false
    isMenuOpen.value = false
  }
  const tl = isMobile.value
    ? gsap
        .timeline()
        .set(overlayPath.value!, {
          attr: { d: 'M 0 0 V 100 Q 50 100 100 100 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.2,
          ease: 'power3.in',
          attr: { d: 'M 0 0 V 70 Q 50 60 100 70 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.2,
          ease: 'power2',
          attr: { d: 'M 0 0 V 0 Q 50 0 100 0 V 0 z' },
          onComplete,
        })
    : gsap
        .timeline()
        .set(overlayPath.value!, {
          attr: { d: 'M 0 0 V 50 Q 50 70 100 50 V 0 z' },
        })
        .to(overlayPath.value!, {
          duration: 0.3,
          ease: 'power2.in',
          attr: { d: 'M 0 0 V 0 Q 50 0 100 0 V 0 z' },
          onComplete,
        })
        .to(
          bodyRef.value!,
          {
            filter: 'blur(0px)',
          },
          0
        )
  tl.to('#menu-1', { y: 0, rotate: 0, ease: 'power2.out', duration: 0.2 }, 0)
    .to('#menu-2', { y: 0, rotate: 0, ease: 'power2.out', duration: 0.2 }, 0)
    .to(
      menuLinks.value!.querySelectorAll('.menu-link'),
      {
        duration: 0.3,
        ease: 'power3.out',
        y: '-100%',
        rotate: -3,
        stagger: { each: 0.05, from: 'end' },
      },
      0
    )
}

tryOnMounted(() => {
  document.querySelector('body')!.style.backgroundColor = mainStore.bgColor
})
</script>

